contracts/credentials/SoulboundCredentia.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title SoulboundCredentials
 * @dev Non-transferable credentials for OAN entities
 */
contract SoulboundCredentials is AccessControl {

    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");

    struct Credential {
        uint256 id;
        address holder;
        address issuer;
        string credentialType;
        string credentialData;  // IPFS hash or JSON
        uint256 issuedAt;
        uint256 expiresAt;
        bool revoked;
        bool soulbound;         // Cannot be transferred
    }

    mapping(uint256 => Credential) public credentials;
    mapping(address => uint256[]) public holderCredentials;
    mapping(address => mapping(string => uint256[])) public credentialsByType;

    uint256 private _nextCredentialId = 1;
    uint256 public totalCredentials;

    event CredentialIssued(
        uint256 indexed credentialId,
        address indexed holder,
        address indexed issuer,
        string credentialType
    );
    event CredentialRevoked(uint256 indexed credentialId, address indexed issuer);
    event TransferAttempted(uint256 indexed credentialId, address from, address to);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ISSUER_ROLE, msg.sender);
    }

    function issueCredential(
        address holder,
        string memory credentialType,
        string memory credentialData,
        uint256 validityDuration,
        bool soulbound
    ) external onlyRole(ISSUER_ROLE) returns (uint256) {
        require(holder != address(0), "Invalid holder");

        uint256 credentialId = _nextCredentialId++;
        uint256 expiresAt = validityDuration == 0 ? 0 : block.timestamp + validityDuration;

        credentials[credentialId] = Credential({
            id: credentialId,
            holder: holder,
            issuer: msg.sender,
            credentialType: credentialType,
            credentialData: credentialData,
            issuedAt: block.timestamp,
            expiresAt: expiresAt,
            revoked: false,
            soulbound: soulbound
        });

        holderCredentials[holder].push(credentialId);
        credentialsByType[holder][credentialType].push(credentialId);
        totalCredentials++;

        emit CredentialIssued(credentialId, holder, msg.sender, credentialType);
        return credentialId;
    }

    function revokeCredential(uint256 credentialId) external {
        Credential storage cred = credentials[credentialId];
        require(cred.issuer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not authorized");
        require(!cred.revoked, "Already revoked");

        cred.revoked = true;

        emit CredentialRevoked(credentialId, msg.sender);
    }

    function transferCredential(uint256 credentialId, address newHolder) external {
        Credential storage cred = credentials[credentialId];
        require(cred.holder == msg.sender, "Not holder");
        require(!cred.soulbound, "Credential is soulbound");
        require(!cred.revoked, "Credential revoked");

        if (cred.soulbound) {
            emit TransferAttempted(credentialId, msg.sender, newHolder);
            revert("Cannot transfer soulbound credential");
        }

        address oldHolder = cred.holder;
        cred.holder = newHolder;

        holderCredentials[newHolder].push(credentialId);
        credentialsByType[newHolder][cred.credentialType].push(credentialId);
    }

    function getCredential(uint256 credentialId) external view returns (Credential memory) {
        return credentials[credentialId];
    }

    function getHolderCredentials(address holder) external view returns (uint256[] memory) {
        return holderCredentials[holder];
    }

    function getCredentialsByType(address holder, string memory credentialType) 
        external view returns (uint256[] memory) {
        return credentialsByType[holder][credentialType];
    }

    function isValid(uint256 credentialId) public view returns (bool) {
        Credential memory cred = credentials[credentialId];
        if (cred.revoked) return false;
        if (cred.expiresAt != 0 && block.timestamp > cred.expiresAt) return false;
        return true;
    }

    function hasCredential(address holder, string memory credentialType) external view returns (bool) {
        uint256[] memory creds = credentialsByType[holder][credentialType];
        for (uint256 i = 0; i < creds.length; i++) {
            if (isValid(creds[i])) return true;
        }
        return false;
    }
}

contracts/identity/DecentralizedIdentity.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title DecentralizedIdentity
 * @dev DID (Decentralized Identifier) system for OAN entities
 */
contract DecentralizedIdentity is Ownable {

    struct DID {
        string identifier;          // did:oan:entity:{id}
        address controller;         // Who controls this DID
        uint256 entityId;          // Link to entity
        bool active;
        uint256 createdAt;
        uint256 updatedAt;
        string metadata;           // IPFS hash to full DID document
    }

    struct DIDDocument {
        string context;
        string didMethod;
        address[] verificationMethods;
        address[] authentication;
        string[] serviceEndpoints;
    }

    // DID string => DID data
    mapping(string => DID) public dids;

    // Address => DID string
    mapping(address => string) public addressToDID;

    // Entity ID => DID string
    mapping(uint256 => string) public entityToDID;

    uint256 public totalDIDs;

    event DIDCreated(string indexed did, address indexed controller, uint256 indexed entityId);
    event DIDUpdated(string indexed did, address indexed controller);
    event DIDDeactivated(string indexed did);
    event ControllerChanged(string indexed did, address indexed oldController, address indexed newController);

    constructor() Ownable(msg.sender) {}

    function createDID(
        uint256 entityId,
        address controller,
        string memory metadata
    ) external returns (string memory) {
        require(bytes(entityToDID[entityId]).length == 0, "DID already exists for entity");

        // Generate DID: did:oan:entity:{entityId}
        string memory did = string(abi.encodePacked("did:oan:entity:", uint2str(entityId)));

        dids[did] = DID({
            identifier: did,
            controller: controller,
            entityId: entityId,
            active: true,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            metadata: metadata
        });

        addressToDID[controller] = did;
        entityToDID[entityId] = did;
        totalDIDs++;

        emit DIDCreated(did, controller, entityId);
        return did;
    }

    function updateDIDMetadata(string memory did, string memory newMetadata) external {
        require(dids[did].controller == msg.sender, "Not controller");
        require(dids[did].active, "DID inactive");

        dids[did].metadata = newMetadata;
        dids[did].updatedAt = block.timestamp;

        emit DIDUpdated(did, msg.sender);
    }

    function transferControl(string memory did, address newController) external {
        require(dids[did].controller == msg.sender, "Not controller");
        require(dids[did].active, "DID inactive");
        require(newController != address(0), "Invalid address");

        address oldController = dids[did].controller;
        dids[did].controller = newController;
        dids[did].updatedAt = block.timestamp;

        delete addressToDID[oldController];
        addressToDID[newController] = did;

        emit ControllerChanged(did, oldController, newController);
    }

    function deactivateDID(string memory did) external {
        require(dids[did].controller == msg.sender || msg.sender == owner(), "Not authorized");
        require(dids[did].active, "Already inactive");

        dids[did].active = false;
        dids[did].updatedAt = block.timestamp;

        emit DIDDeactivated(did);
    }

    function getDID(string memory did) external view returns (DID memory) {
        return dids[did];
    }

    function getDIDByAddress(address addr) external view returns (string memory) {
        return addressToDID[addr];
    }

    function getDIDByEntity(uint256 entityId) external view returns (string memory) {
        return entityToDID[entityId];
    }

    function isActiveDID(string memory did) external view returns (bool) {
        return dids[did].active;
    }

    // Helper function to convert uint to string
    function uint2str(uint256 value) internal pure returns (string memory) {
        if (value == 0) return "0";
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

contracts/identity/ReputationStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ReputationStaking
 * @dev Stake tokens to boost reputation and unlock features
 */
contract ReputationStaking is Ownable, ReentrancyGuard {

    struct Stake {
        uint256 amount;
        uint256 stakedAt;
        uint256 lockedUntil;
        int256 reputationBonus;
        bool active;
    }

    struct StakingTier {
        uint256 minStake;
        uint256 lockDuration;
        int256 reputationMultiplier;  // Basis points (100 = 1%)
        bool enabled;
    }

    mapping(address => Stake) public stakes;
    mapping(uint256 => StakingTier) public tiers;

    uint256 public totalStaked;
    uint256 public totalStakers;

    uint256 public constant BASIS_POINTS = 10000;

    event Staked(address indexed staker, uint256 amount, uint256 tier);
    event Unstaked(address indexed staker, uint256 amount);
    event ReputationBonusApplied(address indexed staker, int256 bonus);
    event TierConfigured(uint256 indexed tier, uint256 minStake, int256 multiplier);

    constructor() Ownable(msg.sender) {
        // Configure default tiers
        tiers[1] = StakingTier(1 ether, 30 days, 500, true);      // Bronze: 5% bonus
        tiers[2] = StakingTier(5 ether, 60 days, 1000, true);     // Silver: 10% bonus
        tiers[3] = StakingTier(10 ether, 90 days, 2000, true);    // Gold: 20% bonus
        tiers[4] = StakingTier(50 ether, 180 days, 5000, true);   // Platinum: 50% bonus
    }

    function configureTier(
        uint256 tier,
        uint256 minStake,
        uint256 lockDuration,
        int256 reputationMultiplier
    ) external onlyOwner {
        tiers[tier] = StakingTier({
            minStake: minStake,
            lockDuration: lockDuration,
            reputationMultiplier: reputationMultiplier,
            enabled: true
        });

        emit TierConfigured(tier, minStake, reputationMultiplier);
    }

    function stake(uint256 tier) external payable nonReentrant {
        require(tiers[tier].enabled, "Tier not enabled");
        require(msg.value >= tiers[tier].minStake, "Insufficient stake");
        require(!stakes[msg.sender].active, "Already staking");

        StakingTier memory tierConfig = tiers[tier];

        stakes[msg.sender] = Stake({
            amount: msg.value,
            stakedAt: block.timestamp,
            lockedUntil: block.timestamp + tierConfig.lockDuration,
            reputationBonus: int256(msg.value * uint256(tierConfig.reputationMultiplier) / BASIS_POINTS),
            active: true
        });

        totalStaked += msg.value;
        totalStakers++;

        emit Staked(msg.sender, msg.value, tier);
        emit ReputationBonusApplied(msg.sender, stakes[msg.sender].reputationBonus);
    }

    function unstake() external nonReentrant {
        Stake storage userStake = stakes[msg.sender];
        require(userStake.active, "No active stake");
        require(block.timestamp >= userStake.lockedUntil, "Stake still locked");

        uint256 amount = userStake.amount;

        userStake.active = false;
        userStake.reputationBonus = 0;

        totalStaked -= amount;
        totalStakers--;

        payable(msg.sender).transfer(amount);

        emit Unstaked(msg.sender, amount);
    }

    function getStake(address staker) external view returns (Stake memory) {
        return stakes[staker];
    }

    function getReputationBonus(address staker) external view returns (int256) {
        if (!stakes[staker].active) return 0;
        return stakes[staker].reputationBonus;
    }

    function isStakeLocked(address staker) external view returns (bool) {
        if (!stakes[staker].active) return false;
        return block.timestamp < stakes[staker].lockedUntil;
    }

    function getTierForAmount(uint256 amount) external view returns (uint256) {
        for (uint256 i = 4; i >= 1; i--) {
            if (tiers[i].enabled && amount >= tiers[i].minStake) {
                return i;
            }
        }
        return 0;
    }
}

