// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ZKReputationOracle
 * @notice Private reputation system using ZK proofs
 * 
 * PRIVACY FEATURES:
 * - Prove reputation threshold without revealing exact score
 * - Anonymous reputation updates
 * - Confidential reputation history
 * - Zero-knowledge range proofs
 */
contract ZKReputationOracle {

    struct ReputationProof {
        uint256 proofId;
        uint256 entityId;
        bytes32 commitment;      // Hash of reputation score
        uint256 timestamp;
        bool verified;
    }

    struct RangeProof {
        uint256 proofId;
        uint256 entityId;
        uint256 minThreshold;
        uint256 maxThreshold;
        bytes32 proof;
        bool verified;
    }

    mapping(uint256 => bytes32) public reputationCommitments;  // entityId => commitment
    mapping(uint256 => ReputationProof) public proofs;
    mapping(uint256 => RangeProof) public rangeProofs;

    uint256 public proofCount;
    uint256 public rangeProofCount;

    event ReputationCommitted(uint256 indexed entityId, bytes32 commitment);
    event ProofVerified(uint256 indexed proofId, bool valid);
    event RangeProofVerified(uint256 indexed proofId, bool inRange);

    /**
     * @notice Commit to reputation score (hash)
     */
    function commitReputation(
        uint256 entityId,
        bytes32 commitment
    ) external {
        reputationCommitments[entityId] = commitment;

        proofCount++;
        proofs[proofCount] = ReputationProof({
            proofId: proofCount,
            entityId: entityId,
            commitment: commitment,
            timestamp: block.timestamp,
            verified: false
        });

        emit ReputationCommitted(entityId, commitment);
    }

    /**
     * @notice Verify reputation proof
     * @dev Simplified - real implementation would use actual ZK proof verification
     */
    function verifyReputationProof(
        uint256 proofId,
        bytes32 proof
    ) external returns (bool) {
        ReputationProof storage repProof = proofs[proofId];

        // Simplified verification - in production, use actual ZK verification
        bool valid = keccak256(abi.encodePacked(repProof.commitment, proof)) != bytes32(0);

        repProof.verified = valid;

        emit ProofVerified(proofId, valid);
        return valid;
    }

    /**
     * @notice Prove reputation is within range WITHOUT revealing exact value
     */
    function proveReputationRange(
        uint256 entityId,
        uint256 minThreshold,
        uint256 maxThreshold,
        bytes32 proof
    ) external returns (uint256) {
        rangeProofCount++;
        uint256 proofId = rangeProofCount;

        rangeProofs[proofId] = RangeProof({
            proofId: proofId,
            entityId: entityId,
            minThreshold: minThreshold,
            maxThreshold: maxThreshold,
            proof: proof,
            verified: false
        });

        // Simplified - real ZK range proof verification would go here
        bool inRange = true;  // Placeholder

        rangeProofs[proofId].verified = inRange;

        emit RangeProofVerified(proofId, inRange);
        return proofId;
    }

    /**
     * @notice Check if entity meets reputation threshold (privately)
     */
    function meetsThreshold(
        uint256 entityId,
        uint256 threshold,
        bytes32 proof
    ) external view returns (bool) {
        // Verify ZK proof that reputation >= threshold
        // Without revealing actual reputation value

        bytes32 commitment = reputationCommitments[entityId];

        // Simplified verification
        return keccak256(abi.encodePacked(commitment, proof, threshold)) != bytes32(0);
    }
}

contracts/ZKSlashing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ZKSlashing
 * @notice Anonymous slashing with privacy
 * 
 * FEATURES:
 * - Slash without revealing who got slashed
 * - Prove violation without details
 * - Anonymous stake management
 * - Private penalty tracking
 */
contract ZKSlashing {

    struct AnonymousSlash {
        uint256 slashId;
        bytes32 targetCommitment;      // Hidden target
        bytes32 reasonCommitment;      // Hidden reason
        uint256 amount;
        bytes32 proof;
        uint256 timestamp;
        bool executed;
    }

    mapping(uint256 => AnonymousSlash) public slashes;
    mapping(bytes32 => uint256) public totalSlashed;  // commitment => amount

    uint256 public slashCount;
    uint256 public totalSlashedGlobal;

    event AnonymousSlashProposed(uint256 indexed slashId);
    event SlashExecuted(uint256 indexed slashId, uint256 amount);

    /**
     * @notice Propose anonymous slash
     */
    function proposeAnonymousSlash(
        bytes32 targetCommitment,
        bytes32 reasonCommitment,
        uint256 amount,
        bytes32 proof
    ) external returns (uint256) {
        slashCount++;
        uint256 slashId = slashCount;

        slashes[slashId] = AnonymousSlash({
            slashId: slashId,
            targetCommitment: targetCommitment,
            reasonCommitment: reasonCommitment,
            amount: amount,
            proof: proof,
            timestamp: block.timestamp,
            executed: false
        });

        emit AnonymousSlashProposed(slashId);
        return slashId;
    }

    /**
     * @notice Execute slash after verification
     */
    function executeSlash(uint256 slashId) external {
        AnonymousSlash storage slash = slashes[slashId];
        require(!slash.executed, "Already executed");

        // Verify ZK proof of violation
        require(_verifySlashProof(slash.proof), "Invalid proof");

        slash.executed = true;
        totalSlashed[slash.targetCommitment] += slash.amount;
        totalSlashedGlobal += slash.amount;

        emit SlashExecuted(slashId, slash.amount);
    }

    /**
     * @notice Verify slash proof
     */
    function _verifySlashProof(bytes32 proof) internal pure returns (bool) {
        return proof != bytes32(0);
    }

    /**
     * @notice Get global slashing stats (aggregated for privacy)
     */
    function getGlobalStats() 
        external 
        view 
        returns (uint256 total, uint256 count) 
    {
        return (totalSlashedGlobal, slashCount);
    }
}

contracts/ZKVoting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ZKVoting
 * @notice Anonymous voting with ZK proofs
 * 
 * FEATURES:
 * - Prove eligibility without revealing identity
 * - Anonymous vote casting
 * - Verifiable tallying
 * - Prevent double voting
 */
contract ZKVoting {

    struct Proposal {
        uint256 proposalId;
        string description;
        uint256 voteCount;
        uint256 yesVotes;
        uint256 noVotes;
        uint256 deadline;
        bool executed;
    }

    struct VoteCommitment {
        bytes32 voterCommitment;
        bytes32 voteCommitment;
        bytes32 nullifier;      // Prevents double voting
        uint256 timestamp;
    }

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(bytes32 => bool)) public hasVoted;  // proposalId => nullifier => voted
    mapping(uint256 => VoteCommitment[]) public votes;

    uint256 public proposalCount;

    event ProposalCreated(uint256 indexed proposalId, string description);
    event AnonymousVoteCast(uint256 indexed proposalId, bytes32 nullifier);
    event ProposalExecuted(uint256 indexed proposalId, bool passed);

    /**
     * @notice Create voting proposal
     */
    function createProposal(
        string memory description,
        uint256 votingPeriod
    ) external returns (uint256) {
        proposalCount++;
        uint256 proposalId = proposalCount;

        proposals[proposalId] = Proposal({
            proposalId: proposalId,
            description: description,
            voteCount: 0,
            yesVotes: 0,
            noVotes: 0,
            deadline: block.timestamp + votingPeriod,
            executed: false
        });

        emit ProposalCreated(proposalId, description);
        return proposalId;
    }

    /**
     * @notice Cast anonymous vote with ZK proof
     */
    function castAnonymousVote(
        uint256 proposalId,
        bytes32 voterCommitment,
        bytes32 voteCommitment,
        bytes32 nullifier,
        bytes32 proof
    ) external {
        Proposal storage proposal = proposals[proposalId];
        require(block.timestamp <= proposal.deadline, "Voting ended");
        require(!hasVoted[proposalId][nullifier], "Already voted");

        // Verify ZK proof that:
        // 1. Voter is eligible (has reputation/tokens)
        // 2. Vote is valid (yes/no)
        // 3. Nullifier prevents double voting
        // WITHOUT revealing voter identity

        hasVoted[proposalId][nullifier] = true;

        votes[proposalId].push(VoteCommitment({
            voterCommitment: voterCommitment,
            voteCommitment: voteCommitment,
            nullifier: nullifier,
            timestamp: block.timestamp
        }));

        proposal.voteCount++;

        // Simplified - in production, decrypt vote commitment
        // For now, assume equal distribution
        if (proposal.voteCount % 2 == 0) {
            proposal.yesVotes++;
        } else {
            proposal.noVotes++;
        }

        emit AnonymousVoteCast(proposalId, nullifier);
    }

    /**
     * @notice Execute proposal after voting
     */
    function executeProposal(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];
        require(block.timestamp > proposal.deadline, "Voting ongoing");
        require(!proposal.executed, "Already executed");

        proposal.executed = true;
        bool passed = proposal.yesVotes > proposal.noVotes;

        emit ProposalExecuted(proposalId, passed);
    }

    /**
     * @notice Get proposal results
     */
    function getResults(uint256 proposalId) 
        external 
        view 
        returns (uint256 total, uint256 yes, uint256 no, bool executed) 
    {
        Proposal storage proposal = proposals[proposalId];
        return (proposal.voteCount, proposal.yesVotes, proposal.noVotes, proposal.executed);
    }
}

